INSERT INTO `comment` VALUES (65524, 19216, 9065, '生活是这样的美丽。剧情什么的都很不错，但有一句话我不喜欢，好像是说男人需要考虑很多，承担很多，但小孩和女人不用。用觉得有点性别歧视的意味。', 8, '2017-02-24 09:18:19', 1887, 001, '╯^╰');
