INSERT INTO `comment` VALUES (65971, 19536, 9155, '11年前我还看不懂啊。关于灵性，关于天命，关于价值的本质和身份倒错。与原生家庭和解，与社会身份和解，打破阶层与刻板印象，拥抱平等与爱。多么杰出。', 10, '2012-03-05 00:58:19', 830, 001, '隔壁家的桃太太');
