INSERT INTO `comment` VALUES (67207, 20272, 9073, '短片《调音师》的加长版，但是改编的非常成功，情节多变，反转无数，黑色幽默接连不断。前面兔子的预言影射全片且首尾相连，很牛逼，结局的反转也超棒，拐棍把罐子打到一边正明还是装瞎，那么他是换了谁的眼角膜。。。', 10, '2018-12-26 10:28:32', 2649, 001, '瑶瑶');
