INSERT INTO `comment` VALUES (67704, 20525, 9177, '前半小时错位的黑色幽默就已经有种浓浓的B级山寨感，各种层面的硬伤更是遍布全片，主题过分追求高大上可随后半段莫名的情节发展在逻辑上又充满自我矛盾，结局更是无力得令人发指。。只能看在中段还是有不少亮点的份上勉强给出三星了。。。现在对奉神的心情大概形容就是看完宁浩大劫案受的伤再乘十倍。', 6, '2014-03-16 01:12:14', 698, 001, 'Kaili Urchins');
