INSERT INTO `comment` VALUES (69364, 19140, 9490, '为爱妻遗愿，老钉子户一飞冲天，远赴南美寻梦', 8, '2009-08-06 00:40:44', 827, 001, '木卫二');
INSERT INTO `comment` VALUES (69365, 21406, 9490, '有史以来最强壮的老爷爷和最轻便的房屋~一群最不靠谱的狗和一只引人发笑的怪鸟~外加一位惹人喜爱却戏份不多的老奶奶，组成了一部我大爱的好片！', 10, '2009-07-23 17:19:08', 2495, 001, 'Mr.王™');
INSERT INTO `comment` VALUES (69366, 19802, 9490, '从头哭到尾', 10, '2009-08-05 14:13:27', 1655, 001, '昏金暗玉');
INSERT INTO `comment` VALUES (69367, 19070, 9490, '梦想，多久开始实现都不晚。《冰河世纪》也好，《史瑞克》也好，永远都赶不上皮克斯动画的情怀与意境。', 8, '2009-05-19 01:20:09', 680, 001, '影志');
