INSERT INTO `movie` VALUES (9444, 35371629, '维和防暴队', 2024, '李达超', '吴孟璋 / 嘉莉安', '黄景瑜 / 王一博 / 钟楚曦 / 欧豪 / 朱亚文 / 谷嘉诚 / 赵华为 / 木兰 / 白那日苏 / 余沛杉 / 刘学 / 南伏龙 / 魏冲 / 印小天 / 冯文娟 / 孙伊涵 / 李岷城 / 蒋林燕 / 高舒乔 / 李凯文', '剧情 / 动作', '中国大陆', '汉语普通话', '2024-05-01(中国大陆)', '101', '中国维和警察 / Formed Police Unit', 'tt15073642', 5.3, 120420, 11.4, 11.1, 28.6, 29.1, 19.9, '影片聚焦中国维和警察鲜为人知的生死时刻！应联合国请求、受国家派遣，余卫东（黄景瑜 饰）、杨震（王一博 饰）、丁慧（钟楚曦 饰）等维和警察肩负神圣使命，远赴异国他乡，深入战火纷飞的任务区执行维和任务。他们将要直面恐怖袭击、武装暴动、黑帮横行等种种险境，维和行动刻不容缓……', '35371629-6c2f567d-093f-4299-a537-d578bad85252.webp', '2025-03-28 16:29:02');
